import { MachineConfig, send, Action, assign, actions } from "xstate";


export function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

export function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}



function say_ask_nomatch_timeout(say_this: Action<SDSContext, SDSEvent>): MachineConfig<SDSContext, any, SDSEvent> {
    return ({ 
        initial: 'prompt',
        states: {
            prompt: {
                entry: send((context) => ({ 
                type: "SPEAK",
                value: say_this})),

                on: { ENDSPEECH: 'ask1' }
            },
            ask1: {
                entry: [send('LISTEN'), send ('TIMEOUT1', {delay: 3000})]
            },
            ask2: {
                entry: [send('LISTEN'), send ('TIMEOUT2', {delay: 3000})]
            },
            ask3: {
                entry: [send('LISTEN'), send ('TIMEOUT3', {delay: 3000})]
            },
            ask4: {
                entry: [send('LISTEN'), send ('TIMEOUT4', {delay: 3000})]
            },
            nomatch: {
                entry: say('Sorry, I did not manage to understand this one. Please repeat.'),
                on: {ENDSPEECH: "ask2"}
            },
            timeout1: {
                entry: say('I did not hear you this time, please speak louder.'),
                on: {ENDSPEECH: "ask2"}
            },
            timeout2: {
                entry: say('I still cannot hear you, please try again.'),
                on: {ENDSPEECH: "ask3"}
            },
            timeout3: {
                entry: say('Sorry, still nothing.'),
                on: {ENDSPEECH: "ask4"}
            },
            timeout4: {
                //Add idle state after 3 reprompts//
                entry: say('Returning to idle.'),
                on: {ENDSPEECH: "idle"}
            },
            idle:{
                type: 'final'
            },
            to_do:{
                entry: say('Sorry, this tool has not been developed yet.')
            },
            specific:{
                entry: say("Please be more specific"),
                on: {ENDSPEECH: "ask2"}
            },
            canceled:{
                entry: say("Your appointment has been canceled.")
            }
        }
    })
}

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const rasaurl = 'https://rafalappointment.herokuapp.com/model/parse'
const nluRequest = (text: string) =>
    fetch(new Request(proxyurl + rasaurl, {
        method: 'POST',
        headers: { 'Origin': 'http://maraev.me' }, // only required with proxy
