import { MachineConfig, send, Action, assign } from "xstate";


function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string } } = {
    "John": { person: "John Appleseed" },
    "Jack": { person: "Jack Jackson" },
    "Liana": { person: "Liana Jelena" },
    "Natalie": { person: "Natalie Portman" },
    "Bruce": { person: "Bruce W" },
    "Bob": { person: "Bob the builder" },
    "Shrek": { person: "Shrek" },

    "on Monday": {day: "Monday" },
    "on Tuesday": {day: "Tuesday" },
    "on Wednesday": {day: "Wednesday" },
    "on Thursday": {day: "Thursday" },
    "on Friday": {day: "Friday" },
    "on Satudary": {day: "Satuday" },
    "on Sunday": {day: "Sunday" },

    "10": { time: "10:00" },
    "11": {time: "11:00"},
    "12": {time: "12:00"},
    "13": {time: "13:00"},
    "14": {time: "14:00"},
    "15": {time: "15:00"},
    "16": {time: "16:00"},
    "17": {time: "17:00"},
    "18": {time: "18:00"},
    "19": {time: "19:00"},


}

const boolean_grammar: {[index: string]: {agreement?: boolean, disagreement?: boolean}} = {
    "yes": {agreement: Boolean(true)},
    "of course": {agreement: Boolean(true)},
    "sure": {agreement: Boolean(true)},
    "no": {disagreement: Boolean(false)},
    "no way": {disagreement: Boolean(false)},
    "absolutely not": {disagreement: Boolean(false)}
}

export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'welcome'
            }
        },
        welcome: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => {return{ day: grammar[context.recResult].day}}),
                    target: "length"
                },
                {target: ".nomatch"}]
             },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person}. On which day is your meeting?`})),
                    on: {ENDSPEECH: "ask"}
                    },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I don't understand. Please repeat"),
                    on: {ENDSPEECH: "ask"}
                }
            }
        },
        length: {
            initial: "prompt" ,
            on: {
                RECOGNISED: [{
                    cond: (context) => "agreement" in (boolean_grammar[context.recResult] || {}),
                    target: "whole_day"},

                    {cond: (context) => "disagreement" in (boolean_grammar[context.recResult] || {}),
                    target: "time"},

                {target:".nomatch"}]

                },
            states: {
                prompt: {
                    entry: say("Will it take the whole day?"),
                    on: {ENDSPEECH: "ask"}
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I don't understand. Please repeat"),
                on: {ENDSPEECH: "ask"}
                }
            }
        },
        time: {
            initial: "prompt" ,
            on: {
                RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => {return{ time: grammar[context.recResult].time}}),
                    target: "repetition"
                    },
                {
                target: ".nomatch"
                }]
            },
            states: {
                prompt: {
                    entry: say("What time is your meeting?"),
                    on: {ENDSPEECH: "ask"}
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I don't understand. Please repeat"),
                    on: {ENDSPEECH: "ask"}}
                }
        },
        whole_day: {
            initial: "prompt" ,
            on: {
                RECOGNISED: [{
                    cond: (context) => "agreement" in (boolean_grammar[context.recResult] || {}),
                    target: "confirmation"},
                    {
                    cond: (context) => "disagreement" in (boolean_grammar[context.recResult] || {}),
                    target: "who"},

                {target: ".nomatch"}]
            },
            states:{
                prompt:{
                    entry: send((context) => ({
                        type: "SPEAK",
                        value:`Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`})),
                    on: {ENDSPEECH: "ask"}

                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I don't understand. Please repeat"),
                    on: {ENDSPEECH: "ask"}}
            }
        },
        repetition: {
            initial: "prompt" ,
            on: {
                RECOGNISED: [{
                    cond: (context) => "agreement" in (boolean_grammar[context.recResult] || {}),
                    target: "confirmation"},
                    {
                    cond: (context) => "disagreement" in (boolean_grammar[context.recResult] || {}),
                    target: "who"},

                {target: ".nomatch"}]
            },
            states:{
                prompt:{
                    entry: send((context) => ({
                        type: "SPEAK",
                        value:`Do you want me to create an appointment with ${context.person} on ${context.day} at ${context.time}?`,
                        on: {ENDSPEECH: "ask"}
                    }))

                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, I don't understand. Please repeat"),
                    on: {ENDSPEECH: "ask"}}
            }
        },
        confirmation: {
            initial: "prompt" ,
            on: {
                ENDSPEECH: "who"
            },
            states: {
                prompt: {
                    entry: say("Your appointment has been created!")
                }
            }

        }
    }
})
